mapper.insert:
insert into board (sn, mac,mcu_id, board_type,
  org_id, test_status, create_time, 
  create_user, putin_time, putin_user,test_time,test_user,esn
  )
values (#{sn,jdbcType=VARCHAR}, #{mac,jdbcType=VARCHAR},#{mcuId}, #{boardType,jdbcType=VARCHAR},
  #{orgId,jdbcType=BIGINT}, #{testStatus,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
  #{createUser,jdbcType=BIGINT}, #{putinTime,jdbcType=TIMESTAMP}, #{putinUser,jdbcType=BIGINT}
  , #{testTime,jdbcType=TIMESTAMP}, #{testUser,jdbcType=BIGINT},#{esn}
  )

mapper.select:
select * from board


        and mac =  #{mac}


        and mcu_id = #{mcuId}

SELECT a.*, b. NAME AS orgName, c.`name` AS putinUserName, concat(b. NAME,'-',d. NAME) AS outUserName, f.tag as boardTypeName FROM board a LEFT JOIN org b ON a.org_id = b.id LEFT JOIN USER c ON a.putin_user = c.id LEFT JOIN USER d ON a.out_user = d.id LEFT JOIN dict f on a.board_type = f.val and f.type_id = 1
order by create_time,putin_time,out_time desc
limit #{startIndex},#{pageSize}
